class Solution {
public:
    int minimumOperations(string num) {
        int n = num.size();
        int ans = INT_MAX;
        vector<vector<int>> pos(10);
        for (int i = 0; i < n; ++i) {
            pos[num[i] - '0'].push_back(i);
        }
        vector<pair<int, int>> special = {{0, 0}, {2, 5}, {5, 0}, {7, 5}};
        for (auto &p : special) {
            int last = -1, ops = 0;
            bool valid = true;
            for (int digit : {p.second, p.first}) {
                auto it = upper_bound(pos[digit].begin(), pos[digit].end(), last);
                if (it == pos[digit].end()) {
                    valid = false;
                    break;
                } else {
                    ops += it - pos[digit].begin();
                    last = *it;
                }
            }
            if (valid) {
                ans = min(ans, ops + n - 1 - last);
            }
        }
        return ans == INT_MAX ? n : ans;
    }
};
