class Solution {
public:
    int minimumOperations(string num) {
        int n = num.size();
        int ans = n; // Start with the worst case, needing to remove all digits
        for (int last_digit : {0, 5}) { // Only 0 and 5 are valid last digits for a number divisible by 25
            for (int i = n - 1; i >= 0; --i) {
                if (num[i] - '0' == last_digit) {
                    for (int j = i - 1; j >= 0; --j) {
                        int second_last_digit = num[j] - '0';
                        if ((last_digit == 0 && second_last_digit == 0) || 
                            (last_digit == 5 && (second_last_digit == 2 || second_last_digit == 7))) {
                            ans = min(ans, (n - j - 1) - 2); // Total operations = total length - current position - 2 digits we keep
                            break;
                        }
                    }
                    break; // Only consider the last occurrence of a valid last digit
                }
            }
        }
        return ans == n ? n - 1 : ans; // If no operation is done, the only operation needed is to remove the first digit to make it 0
    }
};
