int minimumOperations(char* num) {
    int len = strlen(num);
    int ans = INT_MAX; // Start with the maximum possible value

    // Arrays to store the last occurrence of required digits
    int last[2][10]; // [0][*] for '0', [1][*] for '5'
    memset(last, -1, sizeof(last));

    for (int i = 0; i < len; ++i) {
        int d = num[i] - '0';
        if (d % 2 == 0) { // Update last occurrence for even digits
            last[0][d] = i;
        } else { // Update last occurrence for '5'
            last[1][d] = i;
        }

        // Check for '00' or '50' pattern
        if (d == 0 && last[0][0] != -1 && last[0][0] < i) {
            ans = fmin(ans, len - 1 - i + i - last[0][0]);
        }
        if (d == 5 && last[0][0] != -1) {
            ans = fmin(ans, len - 1 - i + i - last[0][0]);
        }

        // Check for '25' or '75' pattern
        if (d == 5 && last[0][2] != -1) {
            ans = fmin(ans, len - 1 - i + i - last[0][2]);
        }
        if (d == 5 && last[0][7] != -1) {
            ans = fmin(ans, len - 1 - i + i - last[0][7]);
        }
    }

    return ans == INT_MAX ? len : ans - 1; // Adjust the answer considering the total length
}
