-spec minimum_operations(Num :: unicode:unicode_binary()) -> integer().
minimum_operations(Num) ->
    Str = unicode:characters_to_list(Num),
    Len = length(Str),
    MinOps = fun F([], _25, _50, _75, _00, Min) -> Min;
                 F([H|T], _25, _50, _75, _00, Min) ->
                    NewMin = case H of
                                 $0 -> if _00 > 0 -> Min - 1;
                                         true -> Min
                                       end;
                                 $2 -> if _25 > 0; _50 > 0 -> Min - 1;
                                         true -> Min
                                       end;
                                 $5 -> if _75 > 0; _00 > 1 -> Min - 1;
                                         true -> Min
                                       end;
                                 $7 -> if _25 > 0 -> Min - 1;
                                         true -> Min
                                       end;
                                 _ -> Min
                             end,
                    F(T,
                      if H == $2 -> _25 + 1; true -> _25 end,
                      if H == $5 -> _50 + 1; true -> _50 end,
                      if H == $7 -> _75 + 1; true -> _75 end,
                      if H == $0 -> _00 + 1; true -> _00 end,
                      NewMin)
             end,
    MinOps(Str, 0, 0, 0, 0, Len).
