func minimumOperations(num string) int {
    result := len(num) // Worst case scenario: delete all digits
    // Check for every pair that makes the number special when it ends with that pair
    pairs := []string{"00", "25", "50", "75"}

    for _, pair := range pairs {
        i, j := len(num)-1, 1
        steps := 0
        for i >= 0 && j >= 0 {
            // Find the second digit of the pair
            if num[i] == pair[1] {
                j--
                if j < 0 { break }
            }
            // Move to the next digit
            i--
            steps++
        }
        for ; i >= 0; i-- {
            // Find the first digit of the pair
            if num[i] == pair[0] {
                result = min(result, steps)
                break
            }
            steps++
        }
    }
    return result
}

func min(a, b int) int {
    if a < b {
        return a
    }
    return b
}
