class Solution {
    public int minimumOperations(String num) {
        int len = num.length();
        int minOps = Integer.MAX_VALUE; // Initialize with maximum value

        // To make the number special, it must end in 00, 25, 50, or 75
        // Check every combination of two digits in the string
        for (int i = 0; i < len; i++) {
            for (int j = i + 1; j < len; j++) {
                int lastDigit = num.charAt(j) - '0';
                int secondLastDigit = num.charAt(i) - '0';
                int pair = secondLastDigit * 10 + lastDigit;

                // Check if the pair forms a special number
                if (pair == 0 || pair == 25 || pair == 50 || pair == 75) {
                    // Calculate operations: total length - 2 (for the digits in the pair) + i (digits between the start and the second last digit)
                    // - (j - i - 1) (digits between the second last and last digit)
                    int ops = len - 2 + i - (j - i - 1);
                    minOps = Math.min(minOps, ops);
                }
            }
        }

        // If no combination forms a special number, return the operations needed to delete all digits
        return minOps == Integer.MAX_VALUE ? len : minOps;
    }
}
