class Solution {
    fun minimumOperations(num: String): Int {
        val n = num.length
        var ans = n // Maximum operations needed would be the length of the string.
        for (i in n - 1 downTo 0) {
            for (j in i - 1 downTo 0) {
                if ((num[i] == '0' || num[i] == '5') && ((num[j] == '2' && num[i] == '5') || (num[j] == '7' && num[i] == '5') || (num[j] == '0' && num[i] == '0') || (num[j] == '5' && num[i] == '0'))) {
                    // Calculate the number of operations needed to make the number special
                    val ops = (n - 1 - i) + (i - 1 - j)
                    ans = minOf(ans, ops)
                }
            }
        }
        return if (ans == n) n - 1 else ans // If no operations were updated, the minimum operations would be to delete all digits except one.
    }
}
