class Solution:
    def minimumOperations(self, num: str) -> int:
        # Initialize the minimum number of operations to a large value
        min_ops = float('inf')
        # Iterate through the possible pairs that make the number special when ending with them
        for pair in ['00', '25', '50', '75']:
            i, j = len(num) - 1, len(num) - 1
            # Find the last digit in the pair
            while i >= 0 and num[i] != pair[1]:
                i -= 1
            # Find the second last digit in the pair
            while j >= 0 and (j == i or num[j] != pair[0]):
                j -= 1
            # If both digits are found and the second last digit's index is not negative
            if j >= 0:
                # Calculate the number of operations needed
                # (length of the string - 1 - last digit's index) + (last digit's index - second last digit's index - 1)
                min_ops = min(min_ops, (len(num) - 1 - i) + (i - j - 1))
        # If min_ops was not updated, return the length of the string minus 1; otherwise, return min_ops
        return len(num) - 1 if min_ops == float('inf') else min_ops
