object Solution {
    def minimumOperations(num: String): Int = {
        val endsWith = List("00", "25", "50", "75")
        var minOps = Int.MaxValue

        for (end <- endsWith) {
            var ops = 0
            var i = num.length - 1
            for (c <- end.reverse) {
                while (i >= 0 && num(i) != c) {
                    ops += 1
                    i -= 1
                }
                if (i < 0) {
                    ops = Int.MaxValue
                    i = -1 // break the loop
                } else {
                    i -= 1 // move past the found character
                }
            }
            minOps = Math.min(minOps, ops)
        }

        if (minOps == Int.MaxValue) 0 else minOps
    }
}
