function minimumOperations(num: string): number {
    let result = num.length; // Initial result with maximum possible operations
    const checkSpecial = (suffix: string) => {
        let ops = 0, idx = num.length - 1;
        for (let i = suffix.length - 1; i >= 0; i--, idx--) {
            while (idx >= 0 && num[idx] !== suffix[i]) {
                ops++; // Increment operations if current digit doesn't match
                idx--;
            }
            if (idx < 0) return Number.MAX_SAFE_INTEGER; // If not found, return max ops
        }
        return ops + idx + 1; // Return total operations needed including trimming leading part
    };
    result = Math.min(result, checkSpecial("00"), checkSpecial("25"), checkSpecial("50"), checkSpecial("75"));
    return result === num.length ? 1 : result; // If num is a single digit (not special), return 1, else return calculated ops
}
