class Solution {
public:
    int makeTheIntegerZero(int num1, int num2) {
        if (num2 == 0) return -1; // Impossible if num2 is 0 since we cannot change num1
        int operations = 0;
        while (num1 != 0 && operations <= 60) {
            long long i = 0;
            // Find the largest 2^i such that 2^i + num2 <= num1
            while ((1LL << i) + num2 <= num1 && i <= 60) i++;
            // If we can't find such i, or it exceeds num1, reduce i to make the subtraction possible
            if (i > 0 && (1LL << i) + num2 > num1) i--;
            if (i > 60 || (1LL << i) + num2 > num1) return -1; // If i is out of bounds or operation invalid, return -1
            num1 -= ((1LL << i) + num2); // Perform the operation
            operations++;
        }
        return operations > 60 ? -1 : operations; // If more than 60 operations, return -1, else return the count
    }
};
