class Solution {
public:
    int makeTheIntegerZero(int num1, int num2) {
        if (num2 == 0) return -1; // If num2 is 0, operation has no effect on num1
        int operations = 0;
        while (num1 != 0 && operations < 61) {
            long long i = 60;
            bool found = false;
            // Iterate from the largest possible i down to 0 to find a valid operation
            while (i >= 0 && !found) {
                long long operation = (1LL << i) + num2;
                if (num1 - operation >= 0 || num1 - operation == num1) {
                    num1 -= operation;
                    operations++;
                    found = true;
                } else {
                    i--;
                }
            }
            if (!found) return -1; // If no valid operation found, return -1
        }
        return num1 == 0 ? operations : -1; // Check if num1 has been reduced to 0
    }
};
