int makeTheIntegerZero(int num1, int num2) {
    long long target = num1, adder = num2;
    int operations = 0;

    while (target != 0 && operations < 64) {
        if (adder == 0) return -1; // If adder is 0, impossible to reach 0

        long long operationValue = 1; // Start with 2^0
        int i = 0;
        for (; i <= 60; ++i) {
            if ((operationValue << i) + adder > target || (operationValue << i) + adder <= 0) break;
        }

        // Perform the operation with the largest value of i that does not exceed target
        if (i > 0) {
            target -= ((operationValue << (i - 1)) + adder);
            operations++;
        } else {
            // If no such i can be found that makes progress, break
            break;
        }
    }

    if (target == 0) return operations;
    else return -1;
}
