func makeTheIntegerZero(num1 int, num2 int) int {
    if num2 == 0 {
        return -1
    }

    operations := 0
    for num1 != 0 && operations <= 60 {
        modified := false
        for i := 0; i <= 60; i++ {
            operationValue := (1 << i) + num2
            if num1-operationValue == 0 {
                num1 -= operationValue
                operations++
                return operations
            } else if abs(num1-operationValue) < abs(num1) {
                num1 -= operationValue
                operations++
                modified = true
                break
            }
        }
        if !modified {
            return -1
        }
    }

    if num1 != 0 {
        return -1
    }
    return operations
}

func abs(x int) int {
    if x < 0 {
        return -x
    }
    return x
}
