impl Solution {
    pub fn make_the_integer_zero(mut num1: i32, num2: i32) -> i32 {
        if num2 == 0 {
            return -1;
        }

        let mut operations = 0;
        while num1 != 0 {
            if num2 > 0 && num1 < num2 {
                return -1;
            }

            let mut i = 0;
            while i <= 60 {
                if 2_i32.pow(i) > num1.abs() + num2.abs() {
                    break;
                }
                i += 1;
            }
            i -= 1;

            num1 -= 2_i32.pow(i) + num2;
            operations += 1;
            if operations > 1_000_000 { // safety check to prevent infinite loops
                return -1;
            }
        }

        operations
    }
}
