impl Solution {
    pub fn make_the_integer_zero(num1: i32, num2: i32) -> i32 {
        let mut num1 = num1;
        let mut operations = 0;

        while num1 > 0 {
            let mut i = 0;
            while i <= 60 {
                if num1 >= (2_i32.pow(i) + num2).abs() {
                    break;
                }
                i += 1;
            }
            if i == 0 && num1 < (2_i32.pow(i) + num2).abs() {
                return -1;
            }
            i = i.max(0);
            num1 -= 2_i32.pow(i.max(0) as u32) + num2;
            operations += 1;
            if operations > 1000 { // Prevent infinite loop
                return -1;
            }
        }

        if num1 < 0 {
            return -1;
        }

        operations
    }
}
