object Solution {
    def makeTheIntegerZero(num1: Int, num2: Int): Int = {
        if (num2 == 0) return if (num1 == 0) 0 else -1
        var ops = 0
        var n1 = num1
        while (n1 != 0 && ops <= 60) {
            var found = false
            for (i <- 60 to 0 by -1 if !found) {
                val delta = (1 << i) + num2
                if (n1 - delta >= 0 || (i == 0 && n1 - delta == 0)) {
                    n1 -= delta
                    ops += 1
                    found = true
                }
            }
            if (!found) return -1
        }
        ops
    }
}
