using System;
using System.Linq;

public class Solution {
    public int MinOperations(int[] nums1, int[] nums2) {
        int operations = 0;
        int n = nums1.Length;
        int maxNums1 = nums1.Max();
        int maxNums2 = nums2.Max();

        // If both conditions are already satisfied
        if (nums1[n - 1] == maxNums1 && nums2[n - 1] == maxNums2) return 0;

        // If either condition is not satisfied and can be fixed by a single swap
        if ((nums1[n - 1] < maxNums1 && nums2[n - 1] < maxNums2) && (maxNums1 == nums2[n - 1] && maxNums2 == nums1[n - 1])) {
            return 1; // Swap the last elements to satisfy both conditions
        }

        // Operations needed to make nums1[n - 1] = max(nums1)
        if (nums1[n - 1] != maxNums1) {
            int index = Array.IndexOf(nums2, maxNums1);
            if (index != -1) { // Swap if possible
                Swap(nums1, nums2, index, n - 1);
                operations++;
            }
        }

        // Operations needed to make nums2[n - 1] = max(nums2)
        if (nums2[n - 1] != maxNums2) {
            int index = Array.IndexOf(nums1, maxNums2);
            if (index != -1) { // Swap if possible
                Swap(nums1, nums2, index, n - 1);
                operations++;
            }
        }

        // Check if both conditions are satisfied after operations
        if (nums1[n - 1] == nums1.Max() && nums2[n - 1] == nums2.Max()) {
            return operations;
        }

        return -1; // If it's impossible to satisfy both conditions
    }

    private void Swap(int[] nums1, int[] nums2, int i, int n) {
        int temp = nums1[i];
        nums1[i] = nums2[i];
        nums2[i] = temp;
    }
}
