#include <vector>
#include <algorithm>

class Solution {
public:
    int minOperations(std::vector<int>& nums1, std::vector<int>& nums2) {
        int n = nums1.size();
        if (nums1[n - 1] == *std::max_element(nums1.begin(), nums1.end()) &&
            nums2[n - 1] == *std::max_element(nums2.begin(), nums2.end()))
            return 0; // No operations needed

        int ops = 0;
        for (int i = 0; i < n - 1; ++i) {
            if (nums1[i] > nums1[n - 1] || nums2[i] > nums2[n - 1]) {
                if (nums1[i] > nums2[i] && nums1[i] > nums1[n - 1] && nums2[i] < nums2[n - 1]) {
                    std::swap(nums1[i], nums2[i]);
                    ops++;
                } else if (nums2[i] > nums1[i] && nums2[i] > nums2[n - 1] && nums1[i] < nums1[n - 1]) {
                    std::swap(nums1[i], nums2[i]);
                    ops++;
                }
            }
        }

        // Check if the conditions are satisfied after the operations
        if (nums1[n - 1] == *std::max_element(nums1.begin(), nums1.end()) &&
            nums2[n - 1] == *std::max_element(nums2.begin(), nums2.end()))
            return ops;

        // If conditions for nums1 and nums2 max are not satisfied simultaneously
        if (nums1[n - 1] < *std::max_element(nums1.begin(), nums1.end()) ||
            nums2[n - 1] < *std::max_element(nums2.begin(), nums2.end()))
            return -1;

        return ops;
    }
};
