class Solution {
    public int minOperations(int[] nums1, int[] nums2) {
        int n = nums1.length;
        int maxIndexNums1 = 0, maxIndexNums2 = 0;

        // Find indexes of maximum values in both arrays
        for (int i = 1; i < n; i++) {
            if (nums1[i] > nums1[maxIndexNums1]) {
                maxIndexNums1 = i;
            }
            if (nums2[i] > nums2[maxIndexNums2]) {
                maxIndexNums2 = i;
            }
        }

        int operations = 0;
        // If the max value is not at the last position, swap is required
        if (maxIndexNums1 != n - 1 || maxIndexNums2 != n - 1) {
            // If the max values are already in the correct positions in both arrays
            if (maxIndexNums1 == n - 1 && nums2[n - 1] >= nums2[maxIndexNums2]) {
                return 0; // No operation needed
            }
            if (maxIndexNums2 == n - 1 && nums1[n - 1] >= nums1[maxIndexNums1]) {
                return 0; // No operation needed
            }
            // Swap to make the last elements the max of their respective arrays
            if (nums1[maxIndexNums1] != nums1[n - 1] || nums2[maxIndexNums2] != nums2[n - 1]) {
                int temp = nums1[maxIndexNums1];
                nums1[maxIndexNums1] = nums2[maxIndexNums2];
                nums2[maxIndexNums2] = temp;
                operations++;
            }
        }

        // After swap, check if the conditions are satisfied
        boolean condition1 = nums1[n - 1] >= nums1[maxIndexNums1];
        boolean condition2 = nums2[n - 1] >= nums2[maxIndexNums2];

        if (!condition1 || !condition2) {
            // If still not satisfied, check if swapping the current last elements helps
            if (nums1[n - 1] < nums2[n - 1] || nums2[n - 1] < nums1[n - 1]) {
                int temp = nums1[n - 1];
                nums1[n - 1] = nums2[n - 1];
                nums2[n - 1] = temp;
                operations++;
            }
        }

        // Final check if both conditions are satisfied
        boolean finalCheck1 = nums1[n - 1] == nums1[maxIndexNums1];
        boolean finalCheck2 = nums2[n - 1] == nums2[maxIndexNums2];
        if (finalCheck1 && finalCheck2) {
            return operations;
        }

        // If it's not possible to satisfy the conditions
        return -1;
    }
}
