class Solution {
    public int minOperations(int[] nums1, int[] nums2) {
        int n = nums1.length;
        // Check if swapping is needed for nums1
        int swapNeeded1 = nums1[n - 1] != getMax(nums1) ? 1 : 0;
        // Check if swapping is needed for nums2
        int swapNeeded2 = nums2[n - 1] != getMax(nums2) ? 1 : 0;

        // If both need swapping but the maximum of one is already in the last position of the other, only one swap is needed
        if (swapNeeded1 + swapNeeded2 == 2) {
            if (nums1[n - 1] == getMax(nums2) || nums2[n - 1] == getMax(nums1)) {
                return 1;
            }
            // If the max of both are not in correct position and cannot be swapped to correct both in one operation
            return 2;
        }
        // If either needs swapping or neither need swapping
        return swapNeeded1 + swapNeeded2;
    }

    private int getMax(int[] nums) {
        int max = nums[0];
        for (int num : nums) {
            if (num > max) {
                max = num;
            }
        }
        return max;
    }
}
