class Solution {
    fun minOperations(nums1: IntArray, nums2: IntArray): Int {
        val n = nums1.size
        var operations = 0

        if (nums1[n - 1] == nums1.maxOrNull() && nums2[n - 1] == nums2.maxOrNull()) {
            return 0
        }

        val swaps = mutableListOf<Pair<Int, Int>>()

        for (i in 0 until n) {
            if ((nums1[i] > nums1[n - 1] && nums2[i] <= nums2[n - 1]) || (nums2[i] > nums2[n - 1] && nums1[i] <= nums1[n - 1])) {
                swaps.add(Pair(nums1[i] - nums1[n - 1], nums2[i] - nums2[n - 1]))
            }
        }

        val sortedSwaps = swaps.sortedWith(compareBy({ -Math.abs(it.first) }, { -Math.abs(it.second) }))

        for (swap in sortedSwaps) {
            if (nums1[n - 1] != nums1.maxOrNull() || nums2[n - 1] != nums2.maxOrNull()) {
                val index = nums1.indexOfFirst { it - nums1[n - 1] == swap.first }
                if (index != -1) {
                    val temp = nums1[index]
                    nums1[index] = nums2[index]
                    nums2[index] = temp
                    operations++
                }
            } else break
        }

        return if (nums1[n - 1] == nums1.maxOrNull() && nums2[n - 1] == nums2.maxOrNull()) operations else -1
    }
}
