class Solution {
    fun minOperations(nums1: IntArray, nums2: IntArray): Int {
        val n = nums1.size
        var opCount = 0

        // Ensure the last elements are maximum in their respective arrays without any operation
        val maxNum1 = nums1.maxOrNull() ?: return -1
        val maxNum2 = nums2.maxOrNull() ?: return -1

        if (nums1[n - 1] == maxNum1 && nums2[n - 1] == maxNum2) {
            return 0 // No operations needed
        }

        // Check if swapping is needed and beneficial
        for (i in 0 until n) {
            if ((nums1[i] != maxNum1 || nums2[i] != maxNum2) && 
                ((nums1[i] > nums1[n - 1] && nums2[i] < nums2[n - 1]) || (nums2[i] > nums2[n - 1] && nums1[i] < nums1[n - 1]))) {
                // Perform swap
                val temp = nums1[i]
                nums1[i] = nums2[i]
                nums2[i] = temp
                opCount++

                // Check if conditions are satisfied after swap
                if (nums1[n - 1] == nums1.maxOrNull() && nums2[n - 1] == nums2.maxOrNull()) {
                    return opCount
                }
            }
        }

        // After all possible beneficial swaps, check if the conditions are met
        if (nums1[n - 1] == nums1.maxOrNull() && nums2[n - 1] == nums2.maxOrNull()) {
            return opCount
        }

        // If conditions cannot be met
        return -1
    }
}
