class Solution {
    fun minOperations(nums1: IntArray, nums2: IntArray): Int {
        // Find max values of both arrays
        val max1 = nums1.maxOrNull()!!
        val max2 = nums2.maxOrNull()!!

        // Initial conditions are already met
        if (nums1.last() == max1 && nums2.last() == max2) return 0

        var operations = 0

        // Check if swapping is necessary and beneficial
        for (i in nums1.indices) {
            val isNum1MaxNotInPlace = nums1.last() != max1 && nums1[i] == max1
            val isNum2MaxNotInPlace = nums2.last() != max2 && nums2[i] == max2

            if (isNum1MaxNotInPlace || isNum2MaxNotInPlace) {
                // Swap the elements
                val temp = nums1[i]
                nums1[i] = nums2[i]
                nums2[i] = temp
                operations++

                // Check if conditions are met after swap
                if (nums1.last() == nums1.maxOrNull()!! && nums2.last() == nums2.maxOrNull()!!) {
                    return operations
                }
            }
        }

        // If after all possible swaps, the conditions are not met
        return -1
    }
}
