impl Solution {
    pub fn min_operations(nums1: Vec<i32>, nums2: Vec<i32>) -> i32 {
        let n = nums1.len();
        let mut operations = 0;

        let mut nums1 = nums1;
        let mut nums2 = nums2;

        // Sort both arrays to easily find and swap to satisfy the conditions
        nums1.sort_unstable();
        nums2.sort_unstable();

        let mut i = n - 1;
        while i > 0 && (nums1[i] < nums1[i - 1] || nums2[i] < nums2[i - 1]) {
            if nums1[i] < nums1[i - 1] && nums2[i] < nums2[i - 1] {
                // If both conditions are violated, it's impossible to satisfy both
                return -1;
            }
            if nums1[i] < nums1[i - 1] {
                // Swap to make nums1[i] the max
                nums1.swap(i, i - 1);
                operations += 1;
            }
            if nums2[i] < nums2[i - 1] {
                // Swap to make nums2[i] the max
                nums2.swap(i, i - 1);
                operations += 1;
            }
            i -= 1;
        }

        // Check if conditions are satisfied
        if nums1[n - 1] != *nums1.iter().max().unwrap() || nums2[n - 1] != *nums2.iter().max().unwrap() {
            return -1;
        }

        operations
    }
}
