object Solution {
    def minOperations(nums1: Array[Int], nums2: Array[Int]): Int = {
        // Initial checks for the already satisfied conditions
        if (nums1.last == nums1.max && nums2.last == nums2.max) return 0

        var operations = 0
        val n = nums1.length

        // Arrays to keep track of swapped indices to avoid double swapping
        val swappedIndices = scala.collection.mutable.Set[Int]()

        // Try to make nums1[n-1] and nums2[n-1] maximum by swapping
        for (i <- 0 until n) {
            if (nums1(i) > nums2(i) && (nums1(i) > nums1(n - 1) || nums2(i) > nums2(n - 1))) {
                if (!swappedIndices.contains(i)) {
                    val temp = nums1(i)
                    nums1(i) = nums2(i)
                    nums2(i) = temp
                    operations += 1
                    swappedIndices.add(i)
                }
            }
        }

        // Check if the conditions are satisfied after swapping
        if (nums1(n - 1) != nums1.max || nums2(n - 1) != nums2.max) {
            // If not, try to correct it by additional swaps if possible
            for (i <- 0 until n) {
                if (nums2(i) > nums1(i) && (nums2(i) > nums2(n - 1) || nums1(i) > nums1(n - 1))) {
                    if (!swappedIndices.contains(i)) {
                        val temp = nums1(i)
                        nums1(i) = nums2(i)
                        nums2(i) = temp
                        operations += 1
                        swappedIndices.add(i)
                    }
                }
            }
        }

        // Final check to see if conditions are met
        if (nums1(n - 1) == nums1.max && nums2(n - 1) == nums2.max) operations else -1
    }
}
