#include <vector>
#include <algorithm>
#include <queue>

using namespace std;

class Solution {
public:
    int minProcessingTime(vector<int>& processorTime, vector<int>& tasks) {
        sort(processorTime.begin(), processorTime.end());
        sort(tasks.rbegin(), tasks.rend()); // Sort tasks in descending order

        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<>> pq; // Min heap

        // Initialize processors in the queue
        for (int i = 0; i < processorTime.size(); ++i) {
            pq.push({processorTime[i], 0}); // Pair<availableTime, tasksProcessed>
        }

        for (int i = 0; i < tasks.size(); ++i) {
            pair<int, int> proc = pq.top();
            pq.pop();
            proc.first = max(proc.first, processorTime[proc.second]) + tasks[i]; // Update available time
            proc.second += 1; // Move to the next processor after every 4 tasks
            if (proc.second == processorTime.size()) { // Reset to the first processor
                proc.second = 0;
            }
            pq.push(proc);
        }

        int maxTime = 0;
        while (!pq.empty()) {
            maxTime = max(maxTime, pq.top().first);
            pq.pop();
        }

        return maxTime;
    }
};
