#include <vector>
#include <algorithm>
#include <queue>

using namespace std;

class Solution {
public:
    int minProcessingTime(vector<int>& processorTime, vector<int>& tasks) {
        sort(tasks.begin(), tasks.end(), greater<int>()); // Sort tasks in descending order
        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq; // Min-heap based on processor availability

        // Initialize the priority queue with the processors' available times
        for (int i = 0; i < processorTime.size(); ++i) {
            pq.push({processorTime[i], i});
        }

        int n = processorTime.size();
        vector<int> maxTime(n, 0); // Tracks the maximum time each processor finishes its tasks

        for (int i = 0; i < tasks.size(); ++i) {
            pair<int, int> p = pq.top();
            pq.pop();
            int startTime = max(p.first, maxTime[p.second]); // Processor start time or the last task's end time, whichever is later
            int endTime = startTime + tasks[i]; // Time when the current task will be finished
            maxTime[p.second] = endTime; // Update the processor's max finish time
            pq.push({endTime, p.second}); // Reinsert processor with the updated end time
        }

        // Find the maximum time across all processors to determine when all tasks have been completed
        return *max_element(maxTime.begin(), maxTime.end());
    }
};
