class Solution {

    /**
     * @param Integer[] $processorTime
     * @param Integer[] $tasks
     * @return Integer
     */
    function minProcessingTime($processorTime, $tasks) {
        sort($tasks); // Sort tasks in ascending order for fair distribution
        sort($processorTime); // Sort processor times in ascending order for optimal scheduling
        $taskChunks = array_chunk($tasks, count($tasks) / count($processorTime)); // Divide tasks equally among processors

        $completionTimes = [];
        for ($i = 0; $i < count($processorTime); $i++) {
            $completionTime = $processorTime[$i] + array_sum($taskChunks[$i]); // Calculate completion time for each processor
            $completionTimes[] = $completionTime;
        }

        return max($completionTimes); // The minimum time when all tasks are done is the max of completion times
    }
}
