class Solution {

    /**
     * @param Integer[] $processorTime
     * @param Integer[] $tasks
     * @return Integer
     */
    function minProcessingTime($processorTime, $tasks) {
        $n = count($processorTime);
        $coreCount = $n * 4; // Total number of tasks to match the number of cores
        $tasksPerProcessor = $coreCount / $n; // Tasks per processor

        // Sort both arrays to optimize task distribution
        rsort($tasks); // Sort tasks in descending order to allocate longer tasks first
        sort($processorTime); // Sort processors by availability time

        $endTime = array_fill(0, $n, 0); // Track end time for each processor

        for ($i = 0; $i < count($tasks); $i++) {
            $processorIdx = $i % $n; // Assign task to processor in round-robin fashion
            $endTime[$processorIdx] = max($endTime[$processorIdx], $processorTime[$processorIdx]) + $tasks[$i];
            // Update processor's next available time
            $processorTime[$processorIdx] = $endTime[$processorIdx];
        }

        return max($endTime); // The time when the last task is completed
    }
}
