#include <vector>
#include <unordered_map>
#include <algorithm>

using namespace std;

class Solution {
public:
    int minimumSeconds(vector<int>& nums) {
        int n = nums.size();
        // Base case: if all elements are the same, no operation is needed.
        if (all_of(nums.begin(), nums.end(), [&](int i){ return i == nums[0]; })) return 0;

        unordered_map<int, int> countMap;
        for (int num : nums) countMap[num]++;

        // Find the maximum frequency of any element.
        int maxFreq = 0;
        for (auto& [_, freq] : countMap) maxFreq = max(maxFreq, freq);

        // The minimum seconds needed is the total number of unique elements
        // minus the maximum frequency of any element, as in the worst case,
        // we can turn all elements into the most frequent element.
        return n - maxFreq;
    }
};
