#include <vector>
#include <unordered_set>
#include <algorithm>

using namespace std;

class Solution {
public:
    int minimumSeconds(vector<int>& nums) {
        int n = nums.size();
        unordered_set<int> uniqueNums(nums.begin(), nums.end());

        // If all elements are the same, no operation is needed.
        if (uniqueNums.size() == 1) return 0;

        // Check if the array is already in a pattern that can be equalized in 1 second.
        for (int i = 0; i < n; ++i) {
            if (nums[i] == nums[(i + 1) % n] || nums[i] == nums[(i - 1 + n) % n]) {
                return 1;
            }
        }

        // If not, it requires 2 seconds at most due to the operation's nature.
        return 2;
    }
};
