#include <limits.h>

int minimumSeconds(int* nums, int numsSize){
    int maxStreak = 1, currentStreak = 1;
    // Check if all elements are initially the same
    for (int i = 1; i < numsSize; i++) {
        if (nums[i] == nums[i - 1]) {
            currentStreak++;
            maxStreak = currentStreak > maxStreak ? currentStreak : maxStreak;
        } else {
            currentStreak = 1;
        }
    }
    // Checking streak across the boundary
    if (nums[0] == nums[numsSize - 1]) {
        int i = 0, j = numsSize - 1;
        while (i < numsSize - 1 && nums[i] == nums[i + 1]) i++;
        while (j > 0 && nums[j] == nums[j - 1]) j--;
        maxStreak = maxStreak > (i + numsSize - j) ? maxStreak : (i + numsSize - j);
    }
    // Calculate minimum seconds needed
    return (maxStreak + 1) / 2;
}
