class Solution {
  int minimumSeconds(List<int> nums) {
    if (nums.toSet().length == 1) return 0; // All elements are already equal
    int maxCount = 1;
    for (int i = 0; i < nums.length; i++) {
      int count = 1;
      for (int j = 1; j < nums.length; j++) {
        int index = (i + j) % nums.length;
        if (nums[index] == nums[(index - 1 + nums.length) % nums.length]) {
          count++;
        } else {
          break;
        }
      }
      maxCount = max(maxCount, count);
    }
    return (nums.length - maxCount + 1) ~/ 2;
  }

  int max(int a, int b) => a > b ? a : b;
}
