func minimumSeconds(nums []int) int {
    maxCount := 1
    currentCount := 1
    n := len(nums)

    // Check for the case where all numbers are the same
    allSame := true
    for i := 1; i < n; i++ {
        if nums[i] != nums[0] {
            allSame = false
            break
        }
    }
    if allSame {
        return 0
    }

    // Extend nums by itself to handle circular nature without modulo operations
    extendedNums := append(nums, nums...)

    for i := 1; i < len(extendedNums); i++ {
        if extendedNums[i] == extendedNums[i-1] {
            currentCount++
            if currentCount > maxCount {
                maxCount = currentCount
            }
        } else {
            currentCount = 1
        }
    }

    // Calculate minimum seconds required
    return (n - maxCount) / 2 + 1
}
