func minimumSeconds(nums []int) int {
 if len(nums) <= 2 {
  return 1
 }

 // Find the longest sequence of repeating elements considering wrap-around
 maxLen := 0
 for i := 0; i < len(nums); i++ {
  count := 1
  for j := 1; j < len(nums); j++ {
   if nums[(i+j)%len(nums)] == nums[i] {
    count++
   } else {
    break
   }
  }
  if count > maxLen {
   maxLen = count
  }
 }

 if maxLen == len(nums) {
  return 0
 }

 // Calculate the minimum seconds
 // Since we're looking for the minimum time to make all elements equal
 // And considering the problem's condition, it's related to the distribution of distinct elements
 // The operation effectively halves the differences between groups of different elements each second
 return len(nums) - maxLen
}
