func minimumSeconds(nums []int) int {
    n := len(nums)
    if n == 1 {
        return 0
    }

    // Create a map to track the distance between same elements
    distanceMap := make(map[int]int)
    for i := 0; i < n; i++ {
        distanceMap[nums[i]] = max(distanceMap[nums[i]], min((i+1)%n, n-(i+1)%n))
    }

    // The answer will be the maximum distance in the map
    maxDistance := 0
    for _, v := range distanceMap {
        if v > maxDistance {
            maxDistance = v
        }
    }

    // Since the problem involves a circular array and picking the maximum distance,
    // we calculate the minimum seconds based on the maximum gap found
    // adjusted by the circular nature of the array.
    return maxDistance
}

func max(a, b int) int {
    if a > b {
        return a
    }
    return b
}

func min(a, b int) int {
    if a < b {
        return a
    }
    return b
}
