/**
 * @param {number[]} nums
 * @return {number}
 */
var minimumSeconds = function(nums) {
    // Function to find the longest sequence where each element can "influence" its neighbors
    const findLongestSequence = (nums) => {
        let res = 1, temp = 1;
        for (let i = 1; i < nums.length; i++) {
            if (nums[i] === nums[i - 1]) {
                temp++;
            } else {
                res = Math.max(res, temp);
                temp = 1;
            }
        }
        // Check for the circular condition where the last and first elements are the same
        if (nums[0] === nums[nums.length - 1]) {
            let i = 1, j = nums.length - 2;
            while (nums[i] === nums[i - 1] && i < nums.length / 2) i++;
            while (nums[j] === nums[j + 1] && j >= nums.length / 2) j--;
            res = Math.max(res, nums.length - (j - i + 1));
        } else {
            res = Math.max(res, temp);
        }
        return res;
    };

    let longestSeq = findLongestSequence(nums);
    if (longestSeq === nums.length) return 0; // All elements are already the same

    let steps = 0;
    while (longestSeq < nums.length) {
        // Double the sequence length each step due to influence
        if (longestSeq * 2 >= nums.length) {
            steps++;
            break;
        }
        longestSeq *= 2;
        steps++;
    }
    return steps;
};
