class Solution {
    fun minimumSeconds(nums: List<Int>): Int {
        if (nums.distinct().size == 1) return 0
        val extendedNums = nums + nums
        var maxEqualSubarray = 0
        var currentEqualSubarray = 1
        for (i in 1 until extendedNums.size) {
            if (extendedNums[i] == extendedNums[i - 1]) {
                currentEqualSubarray += 1
            } else {
                maxEqualSubarray = maxOf(maxEqualSubarray, currentEqualSubarray)
                currentEqualSubarray = 1
            }
        }
        maxEqualSubarray = maxOf(maxEqualSubarray, currentEqualSubarray)
        return (nums.size - maxEqualSubarray + 1) / 2 + 1
    }
}
