class Solution {
    fun minimumSeconds(nums: List<Int>): Int {
        val n = nums.size
        if (nums.toSet().size == 1) return 0

        val prefix = IntArray(n) { 0 }
        val suffix = IntArray(n) { 0 }
        var answer = 2

        prefix[0] = 1
        for (i in 1 until n) {
            if (nums[i] == nums[i - 1]) prefix[i] = prefix[i - 1] + 1
            else prefix[i] = 1
        }

        suffix[n - 1] = 1
        for (i in n - 2 downTo 0) {
            if (nums[i] == nums[i + 1]) suffix[i] = suffix[i + 1] + 1
            else suffix[i] = 1
        }

        for (i in 0 until n) {
            if (i > 0 && nums[i] == nums[i - 1]) continue
            if (i < n - 1 && nums[i] == nums[i + 1]) continue

            val left = if (i == 0) 0 else prefix[i - 1]
            val right = if (i == n - 1) 0 else suffix[i + 1]

            if (left > 0 && right > 0 && nums[(i - 1 + n) % n] == nums[(i + 1) % n]) {
                answer = 1
                break
            }
        }

        return answer
    }
}
