class Solution {
    fun minimumSeconds(nums: List<Int>): Int {
        // A map to keep track of each number's occurrences at each possible index after an operation
        val map = mutableMapOf<Int, MutableSet<Int>>()
        val n = nums.size
        // Initialize the map with the initial indices of each number
        nums.forEachIndexed { index, num ->
            map.getOrPut(num) { mutableSetOf() }.add(index)
        }
        var seconds = 0
        while (map.any { it.value.size < n }) { // Continue until all numbers have spread to all indices
            val newMap = mutableMapOf<Int, MutableSet<Int>>()
            map.forEach { (num, indices) ->
                val newIndices = mutableSetOf<Int>()
                indices.forEach { index ->
                    // For each number, add its current indices and the indices it can spread to
                    newIndices.add(index)
                    newIndices.add((index - 1 + n) % n)
                    newIndices.add((index + 1) % n)
                }
                newMap[num] = newIndices
            }
            if (map == newMap) { // If no change, it's not possible to equalize further
                break
            }
            map.clear()
            map.putAll(newMap) // Prepare for the next iteration
            seconds++
        }
        return seconds
    }
}
