class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function minimumSeconds($nums) {
        $n = count($nums);
        if ($n <= 2) return $n === 2 && $nums[0] !== $nums[1] ? 1 : 0;

        // Find the longest subsequence where consecutive elements are different
        $maxLen = 1;
        $currentLen = 1;
        for ($i = 1; $i < $n; ++$i) {
            if ($nums[$i] !== $nums[$i - 1]) {
                $currentLen++;
            } else {
                $maxLen = max($maxLen, $currentLen);
                $currentLen = 1;
            }
        }
        // Check for circular match
        if ($nums[0] !== $nums[$n - 1] && $currentLen + 1 > $maxLen) {
            for ($i = 1; $i < $n && $nums[$i] !== $nums[$i - 1]; ++$i) {
                $currentLen++;
            }
            $maxLen = max($maxLen, $currentLen);
        }

        // Calculate minimum seconds
        if ($maxLen <= 2) return 1;
        for ($i = 2; ; ++$i) {
            if (pow(2, $i) - 1 >= $n) return $i;
        }
    }
}
