class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function minimumSeconds($nums) {
        $n = count($nums);
        $unique = array_unique($nums);
        if (count($unique) === 1) return 0; // All elements are the same

        // Create a frequency map
        $freq = [];
        foreach ($nums as $num) {
            if (!isset($freq[$num])) {
                $freq[$num] = 1;
            } else {
                $freq[$num]++;
            }
        }

        // Calculate maximum reach in one second
        $maxReach = 0;
        foreach ($freq as $value) {
            $maxReach = max($maxReach, $value);
        }

        // Main logic to calculate minimum seconds
        for ($seconds = 1; ; $seconds++) {
            // Maximum number of elements that can be made equal in 'seconds'
            $reach = 1 + 2 * ($seconds - 1);
            if ($reach >= $n) return $seconds;
        }
    }
}
