class Solution(object):
    def minimumSeconds(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        from collections import Counter
        n = len(nums)
        if n <= 2: return 1 if len(set(nums)) > 1 else 0
        count = Counter(nums)
        max_freq = max(count.values())
        if max_freq == 1: return (n + 1) // 2
        max_seq = 1
        for i in range(n):
            nums.append(nums[i])
        for i in range(1, 2*n):
            if nums[i] == nums[i-1]:
                temp = 2
                while i + 1 < 2*n and nums[i] == nums[i+1]:
                    temp += 1
                    i += 1
                max_seq = max(max_seq, temp)
        return max(1, n - max_seq)
