impl Solution {
    pub fn minimum_seconds(nums: Vec<i32>) -> i32 {
        use std::collections::HashMap;

        // A map to store the frequency of each number.
        let mut freq = HashMap::new();
        // Fill the frequency map.
        for &num in &nums {
            *freq.entry(num).or_insert(0) += 1;
        }

        // Edge case: if all elements are the same, no operation is needed.
        if freq.len() == 1 {
            return 0;
        }

        // Calculate the maximum frequency.
        let max_freq = freq.values().copied().max().unwrap_or(0);
        // Calculate and return the minimum seconds needed.
        // It is calculated based on the maximum frequency of a number in the array.
        ((nums.len() - max_freq) as f64 / (max_freq - 1) as f64).ceil() as i32
    }
}
