impl Solution {
    pub fn minimum_seconds(nums: Vec<i32>) -> i32 {
        let n = nums.len();
        if n <= 2 {
            return 1;
        }
        let mut prefix = vec![false; n];
        let mut suffix = vec![false; n];
        prefix[0] = true;
        suffix[n - 1] = true;
        for i in 1..n {
            if nums[i] == nums[i - 1] {
                prefix[i] = true;
            }
        }
        for i in (0..n - 1).rev() {
            if nums[i] == nums[i + 1] {
                suffix[i] = true;
            }
        }
        for i in 0..n {
            if prefix[i] || suffix[i] {
                return 1;
            }
            if i > 0 && prefix[i - 1] && nums[i] == nums[(i + 1) % n] {
                return 1;
            }
            if i < n - 1 && suffix[i + 1] && nums[i] == nums[(i as isize - 1 + n as isize) as usize % n] {
                return 1;
            }
        }
        2
    }
}
