function minimumSeconds(nums: number[]): number {
    let maxFreq = 0;
    const freq = new Map<number, number>();

    for (const num of nums) {
        freq.set(num, (freq.get(num) || 0) + 1);
        maxFreq = Math.max(maxFreq, freq.get(num)!);
    }

    if (maxFreq === nums.length) return 0; // All elements are already equal

    // The approach is based on the observation that the number of operations required is
    // determined by the maximum frequency of any number in the array, because we can always
    // choose the most frequent number to spread. The rest will be adjusted in the process.

    // Calculate the minimum seconds based on spreading the most frequent number
    let steps = 1;
    while (maxFreq < nums.length) {
        maxFreq *= 2; // Each step can potentially double the presence of the most frequent number
        if (maxFreq < nums.length) steps++; // If after doubling, it's still less than the total, an additional step is needed
    }

    return steps;
}
