#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    int minSizeSubarray(vector<int>& nums, int target) {
        long long sum = 0;
        for (int num : nums) sum += num;
        if (sum == target) return nums.size();
        if (sum < target) return -1;

        int n = nums.size(), minLen = INT_MAX;
        vector<long long> prefixSums(2 * n + 1, 0);
        for (int i = 0; i < 2 * n; i++) {
            prefixSums[i + 1] = prefixSums[i] + nums[i % n];
        }

        unordered_map<long long, int> hashMap;
        for (int i = 0; i < prefixSums.size(); i++) {
            if (hashMap.find(prefixSums[i] - target) != hashMap.end()) {
                minLen = min(minLen, i - hashMap[prefixSums[i] - target]);
            }
            // To handle cases where the sum of the entire array is needed multiple times
            if (hashMap.find(prefixSums[i]) == hashMap.end() || i < hashMap[prefixSums[i]])
                hashMap[prefixSums[i]] = i;
        }

        return minLen == INT_MAX ? -1 : minLen % n == 0 ? n : minLen % n;
    }
};
