-spec min_size_subarray(Nums :: [integer()], Target :: integer()) -> integer().
min_size_subarray(Nums, Target) ->
    MinSize = min_subarray_size(Nums, Target, maps:new(), 0, {0, infinity}),
    case MinSize of
        {_, infinity} -> -1;
        {_, Len} -> Len
    end.

min_subarray_size([], _, Map, Sum, {StartIndex, MinLen}) ->
    case maps:find(Sum, Map) of
        {ok, _} -> {StartIndex, MinLen};
        error -> {StartIndex, MinLen}
    end;
min_subarray_size([H|T], Target, Map, Sum, {StartIndex, MinLen}) ->
    NewSum = Sum + H,
    UpdatedMap = maps:put(NewSum, StartIndex + 1, Map),
    NewMinLen = case maps:find(NewSum - Target, Map) of
        {ok, Index} -> min(MinLen, StartIndex - Index + 2);
        error -> MinLen
    end,
    min_subarray_size(T, Target, UpdatedMap, NewSum, {StartIndex + 1, NewMinLen}).
