func minSizeSubarray(nums []int, target int) int {
    n := len(nums)
    sum := 0
    minLength := n + 1
    left := 0

    for right := 0; right < n; right++ {
        sum += nums[right]
        for sum >= target {
            if right-left+1 < minLength {
                minLength = right - left + 1
            }
            sum -= nums[left]
            left++
        }
    }

    for right := 0; right < n-1; right++ {
        sum += nums[right]
        for sum >= target {
            if right+n-left < minLength {
                minLength = right + n - left + 1
            }
            sum -= nums[left%n]
            left++
        }
    }

    if minLength == n+1 {
        return -1
    }
    return minLength
}
